import Input from "../UI/input";
import classes from "./MealItemForm.module.css";
import { useRef } from "react";
export default function MealForm(props) {
  
  
  return (
    <form className={classes.form}>
      <Input
        type="number"
        value={props.id}
        id={props.id}
        label="Amount"
        min="0"
        max="6"
        defaultValue={1}
      ></Input>
      <button type="submit">+ Add</button>
    </form>
  );
}
