import React from "react";
import ReactDOM from "react-dom";
import Backdrop from "../UI/backdrop";
import { useContext } from "react";
import Variab from "./Context";
import CartItem from "./CartItem";

export default function ItemShop(props) {
  const ctx = useContext(Variab);
  const zmienna = ctx.items.reduce((firstsnap, second) => {
    return firstsnap+second.amount;
  }, 0);
  const itemsy = DUMMY_MEALS.map((meal) => {
    return (
      <CartItem
        key={meal.id}
        price={meal.price}
        name={meal.name}
        description={meal.description}
        id={meal.id}
      />
    );
  });
  return (
    <React.Fragment>
      {ReactDOM.createPortal(
        <Backdrop onHideClick={props.onHide} />,
        document.getElementById("backdrop")
      )}
      {zmienna>=1 ? itemsy: alert('niedziała')}
    </React.Fragment>
  );
}
