import React from "react";
import classes from "./cart.module.css";
import classes2 from "./modal.module.css";
import ReactDOM from "react-dom";
import CartItem from "./CartItem";

function Backdrop(props) {
    console.log(props.onShow);
  return (
    <React.Fragment>
        
      <div className={classes2.backdrop}></div>
      <div className={classes2.modal}>
      <CartItem/>
        <div className={classes.total}>
          <h2>Total:</h2>
        </div>
        <div className={classes.actions}>
          <button className={classes["button--alt"]}>Order</button>
          <button className={classes.button} onClick={props.onHide}>Close</button>
        </div>
      </div>
    </React.Fragment>
  );
}

export default function ItemShop() {
  return (
    <React.Fragment>
      {ReactDOM.createPortal(<Backdrop />, document.getElementById("backdrop"))}
    </React.Fragment>
  );
}
