import React from "react";
import classes from "./CartItem.module.css";
import { useContext } from "react";
import Variab from "./Context";

const CartItem = (props) => {
  const ctx = useContext(Variab);
  const price = `$${props.totalAmount.toFixed(2)}`;
  
  return (
    <React.Fragment key={props.id}>
      <li className={classes["cart-item"]}>
        <div>
          <h2> {ctx.items.name}</h2>
          <div className={classes.summary}>
            <span className={classes.price}>{price}</span>
            <span className={classes.amount}>{ctx.amount}</span>
          </div>
        </div>
        <div className={classes.actions}>
          <button onClick={ctx.Remove}>âˆ’</button>
          <button onClick={ctx.Add}>+</button>
        </div>
      </li>
    </React.Fragment>
  );
};

export default CartItem;
