import React from "react";
import ReactDOM from "react-dom";
import Backdrop from "../UI/backdrop";
import { useContext } from "react";
import Variab from "./Context";
import CartItem from "./CartItem";
import Card from "../UI/Card";

const DUMMY_MEALS = [
  {
    id: "m1",
    name: "Sushi",
    description: "Finest fish and veggies",
    price: 22.99,
  },
  {
    id: "m2",
    name: "Schnitzel",
    description: "A german specialty!",
    price: 16.5,
  },
  {
    id: "m3",
    name: "Barbecue Burger",
    description: "American, raw, meaty",
    price: 12.99,
  },
  {
    id: "m4",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
  },
];
export default function ItemShop(props) {
  const ctx = useContext(Variab);
  const zmienna = ctx.items.reduce((firstsnap, second) => {
    return firstsnap + second.amount;
  }, 0);
  console.log(zmienna);
  const itemsy = DUMMY_MEALS.map((meal) => {
    return (
      <CartItem
        key={meal.id}
        price={meal.price}
        name={meal.name}
        description={meal.description}
        id={meal.id}
      />
    );
  });
  return (
    <React.Fragment>
      {ReactDOM.createPortal(
        <Backdrop onHideClick={props.onHide} />,
        document.getElementById("backdrop")
      )}
      {zmienna >= 1 ? (
        <div className={classes.meals}>
          <Card>
            <ul>{itemsy}</ul>
          </Card>
        </div>
      ) : (
        alert("niedziała")
      )}
    </React.Fragment>
  );
}
